mapper.select:
SELECT
m.id,
m.account_id,
m.class_id,
case when c.class_type = 1 then m.money_pay else m.money_pay end as money,
-- IFNULL(m.money_pay,m.money_income)as money, -- ifnull会导致精度丢失
m.money_introduce,
m.is_total,
m.money_date,
m.deleted,c.class_type "classType",c.class_name "className"
FROM
account_class c
LEFT JOIN account_money m ON c.id = m.class_id


        and m.account_id = #{accountId}


        and m.class_id = #{classId}


        and m.money = #{money}


        and m.money_introduce = #{moneyIntroduce}


        and m.is_total = #{isTotal}


        and m.money_date = #{moneyDate,jdbcType=TIMESTAMP}


order by m.money_date desc
